import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  FormGroup,
  Label,
  Input
} from 'reactstrap';

const Selector = ({ options, selectedCurrencies, setSelectedCurrencies }) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [lastCheckedIndex, setLastCheckedIndex] = useState(null);

  const toggle = () => setDropdownOpen(prevState => !prevState);

  const handleCheckboxChange = (event, index) => {
    const { value, checked } = event.target;
    let newSelectedCurrencies = [...selectedCurrencies];

    if (event.shiftKey && lastCheckedIndex !== null) {
      const start = Math.min(lastCheckedIndex, index);
      const end = Math.max(lastCheckedIndex, index);
      for (let i = start; i <= end; i++) {
        const optionValue = options[i].value;
        if (checked && !newSelectedCurrencies.includes(optionValue)) {
          newSelectedCurrencies.push(optionValue);
        } else if (!checked) {
          newSelectedCurrencies = newSelectedCurrencies.filter(currency => currency !== optionValue);
        }
      }
    } else {
      if (checked) {
        newSelectedCurrencies.push(value);
      } else {
        newSelectedCurrencies = newSelectedCurrencies.filter(currency => currency !== value);
      }
      setLastCheckedIndex(index);
    }

    setSelectedCurrencies(newSelectedCurrencies);
  };

  return (
    <Dropdown isOpen={dropdownOpen} toggle={toggle}>
      <DropdownToggle caret>
        {selectedCurrencies.length > 0 ? selectedCurrencies.join(', ') : 'Select Currencies'}
      </DropdownToggle>
      <DropdownMenu>
        {options.map((option, index) => (
          <DropdownItem key={option.value} toggle={false}>
            <FormGroup check>
              <Label check>
                <Input
                  type="checkbox"
                  value={option.value}
                  checked={selectedCurrencies.includes(option.value)}
                  onChange={(e) => handleCheckboxChange(e, index)}
                />
                {option.label}
              </Label>
            </FormGroup>
          </DropdownItem>
        ))}
      </DropdownMenu>
    </Dropdown>
  );
};

export default Selector;
