// npm install reactstrap bootstrap


import React, { useState } from "react";
import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem, Button, FormGroup, Label, Input } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const Selector = ({ options, selectedCurrencies, setSelectedCurrencies }) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggle = () => setDropdownOpen(prevState => !prevState);

  const handleCheckboxChange = (currency) => {
    if (selectedCurrencies.includes(currency)) {
      setSelectedCurrencies(selectedCurrencies.filter(item => item !== currency));
    } else {
      setSelectedCurrencies([...selectedCurrencies, currency]);
    }
  };

  return (
    <Dropdown isOpen={dropdownOpen} toggle={toggle} direction="down">
      <DropdownToggle caret>
        Select Currencies
      </DropdownToggle>
      <DropdownMenu>
        {options.map(option => (
          <DropdownItem key={option.value} toggle={false}>
            <FormGroup check>
              <Label check>
                <Input 
                  type="checkbox" 
                  checked={selectedCurrencies.includes(option.value)} 
                  onChange={() => handleCheckboxChange(option.value)} 
                />{' '}
                {option.label}
              </Label>
            </FormGroup>
          </DropdownItem>
        ))}
      </DropdownMenu>
    </Dropdown>
  );
};

export default Selector;
